import React, { Component } from 'react'
import { ResponsiveBar } from '@nivo/bar'
// https://nivo.rocks/bar/

class HorizontalBarChart extends Component {

    constructor(props) {
		super(props)
		this.state = {
			data: [
                {
                    "country": "AD",
                    "hot dog": 63,
                    "hot dogColor": "hsl(82, 70%, 50%)",
                    "burger": 142,
                    "burgerColor": "hsl(33, 70%, 50%)",
                    "sandwich": 86,
                    "sandwichColor": "hsl(227, 70%, 50%)",
                    "kebab": 108,
                    "kebabColor": "hsl(295, 70%, 50%)",
                    "fries": 138,
                    "friesColor": "hsl(327, 70%, 50%)",
                    "donut": 142,
                    "donutColor": "hsl(331, 70%, 50%)"
                },
                {
                    "country": "AE",
                    "hot dog": 155,
                    "hot dogColor": "hsl(289, 70%, 50%)",
                    "burger": 154,
                    "burgerColor": "hsl(237, 70%, 50%)",
                    "sandwich": 152,
                    "sandwichColor": "hsl(209, 70%, 50%)",
                    "kebab": 93,
                    "kebabColor": "hsl(215, 70%, 50%)",
                    "fries": 109,
                    "friesColor": "hsl(264, 70%, 50%)",
                    "donut": 57,
                    "donutColor": "hsl(63, 70%, 50%)"
                },
                {
                    "country": "AF",
                    "hot dog": 151,
                    "hot dogColor": "hsl(93, 70%, 50%)",
                    "burger": 87,
                    "burgerColor": "hsl(139, 70%, 50%)",
                    "sandwich": 118,
                    "sandwichColor": "hsl(210, 70%, 50%)",
                    "kebab": 32,
                    "kebabColor": "hsl(94, 70%, 50%)",
                    "fries": 147,
                    "friesColor": "hsl(133, 70%, 50%)",
                    "donut": 161,
                    "donutColor": "hsl(1, 70%, 50%)"
                },
                {
                    "country": "AG",
                    "hot dog": 168,
                    "hot dogColor": "hsl(70, 70%, 50%)",
                    "burger": 139,
                    "burgerColor": "hsl(14, 70%, 50%)",
                    "sandwich": 153,
                    "sandwichColor": "hsl(227, 70%, 50%)",
                    "kebab": 47,
                    "kebabColor": "hsl(266, 70%, 50%)",
                    "fries": 137,
                    "friesColor": "hsl(40, 70%, 50%)",
                    "donut": 101,
                    "donutColor": "hsl(285, 70%, 50%)"
                },
                {
                    "country": "AI",
                    "hot dog": 88,
                    "hot dogColor": "hsl(217, 70%, 50%)",
                    "burger": 67,
                    "burgerColor": "hsl(243, 70%, 50%)",
                    "sandwich": 13,
                    "sandwichColor": "hsl(226, 70%, 50%)",
                    "kebab": 6,
                    "kebabColor": "hsl(44, 70%, 50%)",
                    "fries": 93,
                    "friesColor": "hsl(303, 70%, 50%)",
                    "donut": 28,
                    "donutColor": "hsl(205, 70%, 50%)"
                },
                {
                    "country": "AL",
                    "hot dog": 7,
                    "hot dogColor": "hsl(279, 70%, 50%)",
                    "burger": 22,
                    "burgerColor": "hsl(355, 70%, 50%)",
                    "sandwich": 199,
                    "sandwichColor": "hsl(195, 70%, 50%)",
                    "kebab": 111,
                    "kebabColor": "hsl(161, 70%, 50%)",
                    "fries": 123,
                    "friesColor": "hsl(331, 70%, 50%)",
                    "donut": 136,
                    "donutColor": "hsl(135, 70%, 50%)"
                },
                {
                    "country": "AM",
                    "hot dog": 80,
                    "hot dogColor": "hsl(1, 70%, 50%)",
                    "burger": 79,
                    "burgerColor": "hsl(341, 70%, 50%)",
                    "sandwich": 152,
                    "sandwichColor": "hsl(121, 70%, 50%)",
                    "kebab": 28,
                    "kebabColor": "hsl(90, 70%, 50%)",
                    "fries": 36,
                    "friesColor": "hsl(170, 70%, 50%)",
                    "donut": 49,
                    "donutColor": "hsl(41, 70%, 50%)"
                }
            ]
		}
	}

    render(){
        return(
            <ResponsiveBar
                data={this.state.data}
                keys={[ 'hot dog', 'burger', 'sandwich', 'kebab', 'fries', 'donut' ]}
                indexBy="country"
                margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
                padding={0.15}
                groupMode="grouped"
                layout="horizontal"
                colors={{ scheme: 'nivo' }}
                defs={[
                    {
                        id: 'dots',
                        type: 'patternDots',
                        background: 'inherit',
                        color: '#38bcb2',
                        size: 4,
                        padding: 1,
                        stagger: true
                    },
                    {
                        id: 'lines',
                        type: 'patternLines',
                        background: 'inherit',
                        color: '#eed312',
                        rotation: -45,
                        lineWidth: 6,
                        spacing: 10
                    }
                ]}
                fill={[
                    {
                        match: {
                            id: 'fries'
                        },
                        id: 'dots'
                    },
                    {
                        match: {
                            id: 'sandwich'
                        },
                        id: 'lines'
                    }
                ]}
                borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
                axisTop={null}
                axisRight={null}
                axisBottom={null}
                axisLeft={{
                    tickSize: 0,
                    tickPadding: 11,
                    tickRotation: 0,
                    legend: '',
                    legendPosition: 'middle',
                    legendOffset: -60
                }}
                enableGridY={false}
                labelSkipWidth={12}
                labelSkipHeight={12}
                labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
                legends={[]}
                animate={true}
                motionStiffness={90}
                motionDamping={15}
            />
        )
    }
}

export default HorizontalBarChart
