///////////////////////////////////  AFFICHAGE  //////////////////////////////////
//////////////////////////////////////////////////////////////////////////////////

	// Gère l'affichage d'un élément suggéré (en paramètre)
	searchOptionRendering(option, type) {
		if(type === "artist")
			return option.name
		if(option.title)
			return `${option.title} (chanson de ${option.name} de l'album ${option.albumTitle})`
		else
			return `${option.name} (artiste)`
	}

	topGenreChart() {
		if(this.state.topGenre) {
			return <div>
						<Chart
							id="topGenre"
							chartType="BarChart"
							data={this.state.topGenre}
							width={1000}
							height={700}
							options={{
								title: 'Popularité des genres musicaux',
								//chartArea: {width: '50%'},
								hAxis: {
									title:'Nombre de chansons'
								},
								vAxis: {
									title:'Genre'
								},
								legend: {
									position: "none"
								}
							}} />
					</div> 
		}
	}

	artistChart() {
		const data = this.state.artistData
		if(data) {
			return 
		}
	}

