	// Gère l'affichage d'un élément suggéré (en paramètre)
	searchOptionRendering(option, type) {
		if(type === "artist")
			return option.name
		if(option.title)
			return `${option.title} (chanson de ${option.name} de l'album ${option.albumTitle})`
		else
			return `${option.name} (artiste)`
	}

	topGenreChart() {
		if(this.topGenre) {
			return <div>
						<Chart
							id="topGenre"
							chartType="BarChart"
							data={this.state.topGenre} />
					</div> 
		}
	}

	compareArtistsChart() {
		if(this.state.compareArtistsData) {
			return <div>
						<Chart
							id="compareArtistsChart"
							chartType="BarChart"
							data={this.state.compareArtistsData} />
					</div>
		}
	}

