import React, { Component } from 'react'

import { TextField } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {

			topGenres: [],

			searchAll: "",
			searchAllResult: [],

			searchArtist1: "",
			searchArtist2: "",
			searchArtist1Result: [],
			searchArtist2Result: [],

			searchSongResult: []
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	searchAll(e) {
		const value = e.target.value.trim()
		this.setState({ searchAll: value })
		//console.log("Search: " + value)
		if(value)
			this.getArtistOrSongBySearch(value)
		else
			this.setState({ searchAllResult: [] })
	}

	/*searchArtist(e, fieldId) {
		const value = e.target.value.trim()
		let searchAllResult
		let searchArtistResult
		if(fieldId === "artist1")
			this.setState({ searchArtist1: value })
		else
			this.setState({ searchArtist2: value })
		if(value) {
			this.getArtistOrSongBySearch(value)
			searchAllResult = this.state.searchAllResult
			if(searchAllResult) {
				searchArtistResult = searchAllResult.map()
			}
		}
		else
			this.setState({ searchArtistResult: [] })
	}*/

	/*searchArtist() {
		const searchAllResult = this.state.searchAllResult
		let searchArtistResult = []
		for(option in searchAllResult) {
			if(!option.title) {
				searchArtistResult.push(option)
				console.log("pushed " + option.name)
			}
		}
		this.setState({ searchArtist1Result: searchArtistResult })
		console.log(this.state.searchArtist1Result)
	}*/

	searchArtist() {
		const searchArtistResult = this.state.searchAllResult
		searchArtistResult.map( (item, key) => {
			if(!item.title) {
				console.log(item.name)
				return item
			}
			console.log(item.name + " " + item.title)
			return null
		})
	}

	searchSong(e) {

	}

	getArtistOrSongBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	searchOptionRendering(option) {
		if(option.title) {
			return `${option.title} (chanson de ${option.name} de l'album ${option.albumTitle})`
		}
		else {
			return `${option.name} (artiste)`
		}
	}

	handleSearchOptionSelection(e) {
		const selection = e.target.textContent
		let split
		if(selection) {
			split = selection.split("(")
			if(split[1] === "artiste)")
				console.log("You've searched an artist: " + split[0].trim())
			else
				console.log("You've searched a song: " + split[0].trim())
		}
	}

	/////////////////////////////////////////

	render() {

		return (
			<div className="App" style={{ height : '500px' }}>

				<div>
					<h3>Chercher un artiste ou une chanson</h3>
					<Autocomplete
						freeSolo
						options={ this.state.searchAllResult.map(option => this.searchOptionRendering(option)) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e) }
						renderInput={ params => (
							<TextField
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Recherche"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchAll(e) }
							/>
						) }
					/>
				</div>

				<div>
					<h3>Chercher un artiste</h3>
					<button onClick={ () => this.searchArtist.bind(this) }>GO</button>
					<button onClick={ () => {console.log(this.state.searchArtist1Result)} }>Check</button>
				</div>

			</div>
		)
	}
}

export default App
