import React, { Component } from 'react'

import { TextField } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {
			//hobbies: {},

			artist: '',
			artistData: {},

			topGenres: [],

			searchAll: "",
			searchAllResult: []
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	searchAll(e) {
		const value = e.target.value.trim()
		this.setState({ searchAll: value })
		console.log("Search: " + value)
		if(value)
			this.getArtistOrSongBySearch(value)
		else
			this.setState({ searchAllResult: [] })
	}

	getArtistOrSongBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	searchOptionRendering(option) {
		let res
		if(option.title)
			res = `${option.title} (chanson de ${option.name})`
		else
			res = `${option.name} (artiste)`
		return res
	}

	/*getOptionValue(option) {
		return option
	}*/

	handleSearchOptionSelection(e) {
		const selection = e.target.textContent
		let split
		if(selection) {
			split = selection.split(" ")
			if(split[1] === "(artiste)")
				console.log("You've searched an artist: " + split[0])
			else
				console.log("You've searched a song: " + split[0])
		}
	}

	/////////////////////////////////////////

	render() {

		let artistName = ''
		let data = {}
		if(this.state.artist !== '') {
			artistName = this.state.artist
			data = this.state.artistData
		}

		const searchAllResult = this.state.searchAllResult
		//const searchArtistResult = []
		//const searchSongResult = []

		/////////////////////////////////////////

		return (
			<div className="App" style={{ height : '500px' }}>

				<div>
					<h3>Chercher un artiste ou une chanson</h3>
					<Autocomplete
						freeSolo
						options={ searchAllResult.map(option => this.searchOptionRendering(option)) }
						filterOptions={ (options, state) => { return options } }
						//getOptionLabel={ option => this.getOptionValue(option) }
						onChange={ e => this.handleSearchOptionSelection(e) }
						renderInput={ params => (
							<TextField
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Recherche"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchAll(e) }
							/>
						) }
					/>
				</div>

				<div>
					<h3>Chercher un artiste</h3>
					<input
						type="text"
						ref={ (input) => this.input = input }
					/>
					<button onClick={ () => this.searchArtistData() }>GO</button>
					<p>{artistName}</p>
					<a href={data.urlWikipedia}>{data.urlWikipedia}</a>
				</div>

			</div>
		)
	}
}

export default App
