import React, { Component } from 'react'
import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'
import './App.css'

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {
			//hobbies: {},
			artist: '',
			artistData: {},
			topGenres: []
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	/*addHobby() {
		// faire une copie de l'objet hobbies
		const hobbies = { ...this.state.hobbies };

		// Ajouter le nouveau hobby
		const timestamp = Date.now();
		hobbies[`hobby-${timestamp}`] = this.input.value;
		// equivalent a : hobbies['hobby' + timestamp] = this.state.input;

		// on met a jour l'etat
		this.setState({ hobbies });
	}*/

	/*removeHobby(key) {
		// faire une copie de l'objet hobbies
		const hobbies = { ...this.state.hobbies };

		// Supprime le hobby dont la cle key a ete passee en parametres
		//delete hobbies[key]; // ne marche pas avec firebase
		hobbies[key] = null;

		// Mettre a jour l'etat
		// En ES6 vous pouvez juste taper {'hobbies'} au lieu de {hobbies:hobbies}
		this.setState({ hobbies });
		console.log(this.state.hobbies)
	}*/

	/////////////////////////////////////////

	render() {
		// iterateur sur les proprietes de l'objet
		/*let list = Object.keys(this.state.hobbies).map((key, index) => {
			// key = element courant (propriete courante de l'objet)
			const hobby = this.state.hobbies[key];

			const liStyle = {
				backgroundColor: index % 2 === 0 ? 'lightpink' : 'red'
			};

			return <li key={key} style={liStyle} 
				onClick={() => this.removeHobby(key)}>
				{hobby}
			</li>
		});*/

		let artistName = ''
		let data = {}
		if(this.state.artist !== '') {
			artistName = this.state.artist
			data = this.state.artistData
		}



		/////////////////////////////////////////

		return (
			<div className="App" style={{ height : '500px' }}>
				<h3>Chercher un artiste</h3>
				<input
					type="text"
					ref={(input) => this.input = input}
				/>
				<button onClick={() => this.searchArtistData()}>GO</button>
				<p>{artistName}</p>
				<a href={data.urlWikipedia}>{data.urlWikipedia}</a>



			</div>
		)
	}
}

export default App
