import React, { Component } from 'react'

import { TextField } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {

			topGenres: [],

			searchAll: "",
			searchAllResult: [],

			searchArtist1: "",
			searchArtist1Result: [],

			searchArtist2: "",
			searchArtist2Result: [],

			searchSongResult: []
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	getState() {
		console.log(this.state)
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	/////////////////////////////////////////

	// Requête sur l'API Wasabi : recherche d'artistes et chansons
	getArtistOrSongBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	// Requête sur l'API Wasabi : recherche d'artistes
	getArtistBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchArtist1Result: data.filter(option => !option.title) })
				else
					this.setState({ searchArtist1Result: [] })
				console.log(this.state.searchArtist1Result)
			})
	}

	// Requête sur l'API Wasabi : recherche de chansons
	getSongBySearch(search, songId) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	// Handler de la recherche d'artiste ou chanson (TextField onChange)
	searchAll(e) {
		const value = e.target.value.trim()
		this.setState({ searchAll: value })
		//console.log("Search: " + value)
		if(value)
			this.getArtistOrSongBySearch(value)
		else
			this.setState({ searchAllResult: [] })
	}

	// Handler de la recherche d'artiste (TextField onChange)
	searchArtist(e, artistId) {
		const value = e.target.value.trim()
		if(artistId === "artist1") {
			this.setState({ searchArtist1: value })
		} else {
			this.setState({ searchArtist2: value })
		}
		console.log("Search (" + artistId + "): " + value)
		if(value)
			this.getArtistBySearch(value)
		else {
			if(artistId === "artist1") {
				this.setState({ searchArtist1Result: [] })
			} else {
				this.setState({ searchArtist2Result: [] })
			}
			console.log("[]")
		}
	}
	/*searchArtist(e, artistId) {
		const value = e.target.value.trim()

		if(artistId === "artist1") {
			this.setState({ searchArtist1: value })
		} else {
			this.setState({ searchArtist2: value })
		}
		console.log("Search (" + artistId + "): " + value)

		if(value) {
			this.getArtistBySearch(value, artistId)
				.then((result) => {
					if(artistId === "artist1") {
						this.setState({ searchArtist1Result: result })
					} else {
						this.setState({ searchArtist2Result: result })
					}
				})
		}
		else {
			if(artistId === "artist1") {
				this.setState({ searchArtist1Result: [] })
			} else {
				this.setState({ searchArtist2Result: [] })
			}
			console.log("[]")
		}
	}*/

	// Handler de la recherche de chanson (TextField onChange)
	/*searchSong(searchAllRes) {
		const searchSongResult = []
		for(let i=0; i<searchAllRes.length; i++) {
			if(searchAllRes[i].title) {
				console.log(searchAllRes[i].title + " (chanson) " + searchAllRes[i].name + " (artiste) " + searchAllRes[i].albumTitle + " (album)")
				searchSongResult.push(searchAllRes[i])
			}
		}
		console.log(searchSongResult)
	}*/

	// Gère l'affichage d'un élément suggéré (en paramètre)
	searchOptionRendering(option, type) {
		if(!type) {
			if(option.title)
				return `${option.title} (chanson de ${option.name} de l'album ${option.albumTitle})`
			else
				return `${option.name} (artiste)`
		} 
		else if(type === "artistOnly")
			return option.name
		else if(type === "songOnly")
			return option.title
	}

	// Handler de la sélection d'un élément suggéré
	handleSearchOptionSelection(e, type) {
		let split
		const selection = e.target.textContent
		if(selection) {
			if(!type) {
				split = selection.split("(")
				if(split[1] === "artiste)")
					console.log("You've searched an artist: " + split[0].trim())
				else
					console.log("You've searched a song: " + split[0].trim())
			} else if(type === "artistOnly") {
				console.log("You've searched an artist: " + e.target.textContent)
			} else if(type === "songOnly") {
				console.log("You've searched a song: " + e.target.textContent)
			}
		}
	}

	/////////////////////////////////////////

	render() {

		//console.log(this.state)

		return (
			<div className="App" style={{ height : '500px' }}>

				<button onClick={ this.getState.bind(this) }>Get state</button>

				<div>
					<h3>Chercher un artiste ou une chanson</h3>
					<Autocomplete
						freeSolo
						options={ this.state.searchAllResult.map(option => this.searchOptionRendering(option)) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e) }
						renderInput={ params => (
							<TextField
								id="searchAllTextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Recherche"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchAll(e) }
							/>
						) }
					/>
				</div>

				<div>
					<h3>Comparer deux artistes</h3>
					<Autocomplete
						freeSolo
						options={ this.state.searchArtist1Result.map(option => this.searchOptionRendering(option, "artistOnly")) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e, "artistOnly") }
						renderInput={ params => (
							<TextField
								id="artist1TextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Artiste #1"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchArtist(e, "artist1") }
							/>
						) }
					/>
					<Autocomplete
						freeSolo
						options={ this.state.searchArtist2Result.map(option => this.searchOptionRendering(option, "artistOnly")) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e, "artistOnly") }
						renderInput={ params => (
							<TextField
								id="artist2TextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Artiste #2"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchArtist(e, "artist2") }
							/>
						) }
					/>
					<button>Comparer !</button>
				</div>

			</div>
		)
	}
}

export default App
