import React from 'react';
import './App.css';

import { withStyles, fade } from '@material-ui/core/styles';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Container from '@material-ui/core/Container';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';

const styles = theme => ({
	root: {
		flexGrow: 1,
	},
	container: {
		marginTop: theme.spacing(5),
	},
	tableTitle: {
		padding: theme.spacing(2),
	},
	buttonGroup: {
		margin: theme.spacing(2),
	},
});

class App extends React.Component {

	state = {
		restaurantList: [],
		page: 1,
		rowsPerPage: 10,
	};

	componentDidMount() {
		this.getRestaurantData();
	}

	/*componentDidUpdate(prevState) {
		if(this.state.page !== prevState.page || this.state.rowsPerPage !== prevState.rowsPerPage) {
			this.getRestaurantData();
		}
	}*/

	getRestaurantData = async () => {
		const { page, rowsPerPage } = this.state;
		const request = `http://localhost:8080/api/restaurants?page=${page}&pagesize=${rowsPerPage}`;
		console.log(request);
		const response = await fetch(request);
		const { data } = await response.json();
		this.setState({ restaurantList : data })
	}

	handleChangePage = (event, newPage) => {
		this.setState({page : newPage});
	};

	handleChangeRowsPerPage = event => {
		this.setState({page: 1, rowsPerPage: +event.target.value});
	};

	render() {
		const { classes } = this.props;

		return (
			<div className="App">
				<Container className={classes.container}>
					<Paper>
						<Typography component="h1" variant="h6" color="primary" className={classes.tableTitle}>Restaurants</Typography>
						<Table aria-label="simple table">
							<TableHead>
								<TableRow>
									<TableCell>Name</TableCell>
									<TableCell>Cuisine</TableCell>
									<TableCell>Borough</TableCell>
									<TableCell>Address</TableCell>
								</TableRow>
							</TableHead>
							<TableBody>
								{this.state.restaurantList.map((restaurant, key) => (
									<TableRow key={key}>
									<TableCell>{restaurant.name}</TableCell>
									<TableCell>{restaurant.cuisine}</TableCell>
									<TableCell>{restaurant.borough}</TableCell>
									<TableCell>{restaurant.address.building} {restaurant.address.street}<br />New York, NY {restaurant.address.zipcode}</TableCell>
									</TableRow>
								))}
							</TableBody>
						</Table>
						<ButtonGroup className={classes.buttonGroup}>
							<Button>Prev</Button>
							<Button>Page</Button>
							<Button>Next</Button>
						</ButtonGroup>
					</Paper>
				</Container>
			</div>
		);
	}

}

export default withStyles(styles)(App);
