import React, { Component } from 'react'

import { TextField } from '@material-ui/core';
import { Autocomplete } from '@material-ui/lab';

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {
			//hobbies: {},
			artist: '',
			artistData: {},
			topGenres: [],
			searchAllResult: [
				{
				name: "Iron Maiden",
				picture: "http://e-cdn-images.deezer.com/images/artist/082f3e7d6f3f0ae104a4da93c97273d7/56x56-000000-80-0-0.jpg",
				nameSuggest: {
				input: [
				"Iron Maiden",
				"Maiden"
				],
				weight: 1641346
				}
				},
				{
				name: "Idol Worship International",
				picture: "http://e-cdn-images.deezer.com/images/artist/9f4f6890ab265a4f2f2b8dc5ac006910/56x56-000000-80-0-0.jpg",
				nameSuggest: {
				input: [
				"Idol Worship International",
				"Worship International"
				],
				weight: 2233676
				}
				},
				{
				name: "American Idol",
				picture: "http://e-cdn-images.deezer.com/images/artist/9f4f6890ab265a4f2f2b8dc5ac006910/56x56-000000-80-0-0.jpg",
				nameSuggest: {
				input: [
				"American Idol",
				"Idol"
				],
				weight: 2233646
				}
				},
				{
				name: "Tal & Irma",
				picture: "http://e-cdn-images.deezer.com/images/artist/bf79485167aa163a98bbddfc6acb1e79/56x56-000000-80-0-0.jpg",
				nameSuggest: {
				input: [
				"Tal & Irma",
				"& Irma"
				],
				weight: 342336
				}
				},
				{
				name: "Icona Pop",
				picture: "http://e-cdn-images.deezer.com/images/artist/c2c13088156a137f8fe514cb5c7e6b3b/56x56-000000-80-0-0.jpg",
				nameSuggest: {
				input: [
				"Icona Pop",
				"Pop"
				],
				weight: 557993
				}
				},
				{
				name: "Irma",
				picture: "http://e-cdn-images.deezer.com/images/artist/c54eebf99aebb6ee5714304e729bcc26/56x56-000000-80-0-0.jpg",
				nameSuggest: {
				input: [
				"Irma"
				],
				weight: 146259
				}
				},
				{
				name: "V채rttin채",
				title: "Iro",
				albumTitle: "Kokko",
				picture: "http://e-cdn-images.deezer.com/images/cover/3eb7e46516beb064877e04cc2ed4d806/56x56-000000-80-0-0.jpg",
				weight: 0
				},
				{
				name: "Ryuichi Kawamura",
				title: "Namida Iro",
				albumTitle: "Love",
				picture: "",
				weight: 0
				},
				{
				name: "Coheed And Cambria",
				title: "IRO-Bot",
				albumTitle: "The Second Stage Turbine Blade",
				picture: "http://e-cdn-images.deezer.com/images/cover/003ed54487c8a75777301b36ab0e5542/56x56-000000-80-0-0.jpg",
				weight: 0
				},
				{
				name: "LM. C",
				title: "-SORA Namida Iro-",
				albumTitle: "Glitter Loud Box",
				picture: "",
				weight: 0
				},
				{
				name: "Hayley Westenra",
				title: "Ruri Iro No Chikyu",
				albumTitle: "The Best Of Hayley Sings Japanese Songs",
				picture: "http://e-cdn-images.deezer.com/images/cover/4a3ccd085850a88f35795013493b52ec/56x56-000000-80-0-0.jpg",
				weight: 0
				},
				{
				name: "Cyndi Lauper",
				title: "Gin-Iro No Yume",
				albumTitle: "Other Songs",
				picture: "",
				weight: 0
				}
				]
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	/////////////////////////////////////////

	render() {

		let artistName = ''
		let data = {}
		if(this.state.artist !== '') {
			artistName = this.state.artist
			data = this.state.artistData
		}

		const searchAllResult = this.state.searchAllResult
		//const searchArtistResult = []
		//const searchSongResult = []

		/////////////////////////////////////////

		return (
			<div className="App" style={{ height : '500px' }}>

				<div>
					<h3>Chercher un artiste ou une chanson</h3>
					<Autocomplete
						freeSolo
						options={searchAllResult.map(option => option.name)}
						renderInput={params => (
							<TextField
								{...params}
								//style={{ width : '300px' }}
								label="Recherche"
								margin="normal"
								variant="outlined"
								//fullWidth
							/>
						)}
					/>
				</div>

				<div>
					<h3>Chercher un artiste</h3>
					<input
						type="text"
						ref={(input) => this.input = input}
					/>
					<button onClick={() => this.searchArtistData()}>GO</button>
					<p>{artistName}</p>
					<a href={data.urlWikipedia}>{data.urlWikipedia}</a>
				</div>

			</div>
		)
	}
}

export default App
