import React, { Component } from 'react'

import { makeStyles } from '@material-ui/core/styles';

import { TextField, Paper, InputBase, Divider, IconButton, MenuIcon, SearchIcon, DirectionsIcon } from '@material-ui/core';
import { Autocomplete } from '@material-ui/lab';

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

const useStyles = makeStyles(theme => ({
	root: {
	  padding: '2px 4px',
	  display: 'flex',
	  alignItems: 'center',
	  width: 400,
	},
	input: {
	  marginLeft: theme.spacing(1),
	  flex: 1,
	},
	iconButton: {
	  padding: 10,
	},
	divider: {
	  height: 28,
	  margin: 4,
	},
  }));

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {
			//hobbies: {},

			artist: '',
			artistData: {},

			topGenres: [],

			searchAll: "",
			searchAllResult: []
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	searchAll(e) {
		const value = e.target.value.trim()
		this.setState({ searchAll: value })
		console.log("Search: " + value)

		if(value === "") {
			this.setState({ searchAllResult: [] })
		}
		else {
			this.getArtistOrSongBySearch(value)
		}
	}

	getArtistOrSongBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	test() {
		console.log("input change")
	}

	/////////////////////////////////////////

	render() {

		const classes = useStyles();

		let artistName = ''
		let data = {}
		if(this.state.artist !== '') {
			artistName = this.state.artist
			data = this.state.artistData
		}

		const searchAllResult = this.state.searchAllResult
		//const searchArtistResult = []
		//const searchSongResult = []

		/////////////////////////////////////////

		return (
			<div className="App" style={{ height : '500px' }}>

				<div>
					<h3>Chercher un artiste ou une chanson</h3>
					<Autocomplete
						freeSolo
						options={searchAllResult.map(option => option.name)}
						onInputChange={ () => this.test() }
						renderInput={params => (
							console.log(params),
							<TextField
								{...params}
								inputRef={(input) => this.input = input}
								style={{ width : '300px' }}
								label="Recherche"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchAll(e) }
								autoComplete="false"
							/>
						)}
					/>
				</div>

				<Paper component="form" className={classes.root}>
					<IconButton className={classes.iconButton} aria-label="menu">
						<MenuIcon />
					</IconButton>
					<InputBase
						className={classes.input}
						placeholder="Search Google Maps"
						inputProps={{ 'aria-label': 'search google maps' }}
					/>
					<IconButton type="submit" className={classes.iconButton} aria-label="search">
						<SearchIcon />
					</IconButton>
					<Divider className={classes.divider} orientation="vertical" />
					<IconButton color="primary" className={classes.iconButton} aria-label="directions">
						<DirectionsIcon />
					</IconButton>
				</Paper>

				<div>
					<h3>Chercher un artiste</h3>
					<input
						type="text"
						ref={(input) => this.input = input}
					/>
					<button onClick={() => this.searchArtistData()}>GO</button>
					<p>{artistName}</p>
					<a href={data.urlWikipedia}>{data.urlWikipedia}</a>
				</div>

			</div>
		)
	}
}

export default App
