import React, { Component } from 'react'
import { withStyles } from '@material-ui/core/styles';

import { TextField, Button, AppBar, Toolbar, Typography } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

const styles = theme => ({
	title: {},
	button: {
		margin: 50
	}
})

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {

			topGenres: [],

			search: {
				all: {
					input: "",
					result: [],
					selection: ""
				},
				artist: {
					first: {
						input: "",
						result: [],
						selection: ""
					},
					second: {
						input: "",
						result: [],
						selection: ""
					}
				},
				song: {
					first: {
						input: "",
						result: [],
						selection: ""
					},
					second: {
						input: "",
						result: [],
						selection: ""
					}
				}
			},


			searchAllInput: "",
			searchAllResult: [],
			searchAllSelection: "",

			searchArtist1Input: "",
			searchArtist1Result: [],
			searchArtist1Selection: "",

			searchArtist2Input: "",
			searchArtist2Result: [],
			searchArtist2Selection: "",

			searchSong1Input: "",
			searchSong1Result: [],
			searchSong1Selection: "",

			searchSong2Input: "",
			searchSong2Result: [],
			searchSong2Selection: "",
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	getState() {
		console.log(this.state)
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	/////////////////////////////////////////

	// Requête sur l'API Wasabi : recherche d'artistes et chansons
	getArtistOrSongBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	// Requête sur l'API Wasabi : recherche d'artistes
	getArtistBySearch(search, artistId) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				let searchResult
				if(data != null)
					searchResult = data.filter(option => !option.title)
				else
					searchResult = []
				if(artistId === "artist1")
					this.setState({ searchArtist1Result: searchResult })
				else
					this.setState({ searchArtist2Result: searchResult })
			})
	}

	// Requête sur l'API Wasabi : recherche de chansons
	getSongBySearch(search, songId) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				let searchResult
				if(data != null)
					searchResult = data.filter(option => option.title)
				else
					searchResult = []
				if(songId === "song1")
					this.setState({ searchSong1Result: searchResult })
				else
					this.setState({ searchSong2Result: searchResult })
			})
	}

	// Handler de la recherche d'artiste ou chanson (TextField onChange)
	searchAll(e) {
		const value = e.target.value.trim()
		this.setState({ searchAllInput: value })
		console.log("Search: " + value)
		if(value)
			this.getArtistOrSongBySearch(value)
		else
			this.setState({ searchAllResult: [] })
	}

	// Handler de la recherche d'artiste (TextField onChange)
	searchArtist(e, artistId) {
		const value = e.target.value.trim()
		if(artistId === "artist1")
			this.setState({ searchArtist1Input: value })
		else
			this.setState({ searchArtist2Input: value })
		console.log("Search (" + artistId + "): " + value)
		if(value)
			this.getArtistBySearch(value, artistId)
		else {
			if(artistId === "artist1")
				this.setState({ searchArtist1Result: [] })
			else
				this.setState({ searchArtist2Result: [] })
			console.log("[]")
		}
	}

	// Handler de la recherche de chanson (TextField onChange)
	searchSong(e, songId) {
		const value = e.target.value.trim()
		if(songId === "song1")
			this.setState({ searchSong1Input: value })
		else
			this.setState({ searchSong2Input: value })
		console.log("Search (" + songId + "): " + value)
		if(value)
			this.getSongBySearch(value, songId)
		else {
			if(songId === "song1")
				this.setState({ searchSong1Result: [] })
			else
				this.setState({ searchSong2Result: [] })
			console.log("[]")
		}
	}

	// Gère l'affichage d'un élément suggéré (en paramètre)
	searchOptionRendering(option, type) {
		if(type === "artist")
			return option.name
		if(option.title)
			return `${option.title} (chanson de ${option.name} de l'album ${option.albumTitle})`
		else
			return `${option.name} (artiste)`
	}

	// Handler de la sélection d'un élément suggéré
	handleSearchOptionSelection(e, field, type) {
		//console.log(e.target)
		const selection = e.target.textContent
		if(selection) {
			if(!type) {
				let split = selection.split("(")
				let value = split[0].trim()
				this.setState({ searchAllSelection: value })
				if(split[1] === "artiste)")
					console.log("You've searched an artist: " + value)
				else
					console.log("You've searched a song: " + value)
			}
			else {
				let value = e.target.textContent
				switch(field) {
					case "artist1":
						this.setState({ searchArtist1Selection: value })
						break
					case "artist2":
						this.setState({ searchArtist2Selection: value })
						break
					case "song1":
						this.setState({ searchSong1Selection: value })
						break
					case "song2":
						this.setState({ searchSong2Selection: value })
						break
					default:
				}
				if(type === "artist")
					console.log("You've searched an artist: " + value)
				else if(type === "song")
					console.log("You've searched a song: " + value)
			}
		} else {
			switch(field) {
				case "artist1":
					this.setState({ searchArtist1Input: "" })
					this.setState({ searchArtist1Result: [] })
					this.setState({ searchArtist1Selection: "" })
					break
				case "artist2":
					this.setState({ searchArtist2Input: "" })
					this.setState({ searchArtist2Result: [] })
					this.setState({ searchArtist2Selection: "" })
					break
				case "song1":
					this.setState({ searchSong1Input: "" })
					this.setState({ searchSong1Result: [] })
					this.setState({ searchSong1Selection: "" })
					break
				case "song2":
					this.setState({ searchSong2Input: "" })
					this.setState({ searchSong2Result: [] })
					this.setState({ searchSong2Selection: "" })
					break
				default:
					this.setState({ searchAllInput: "" })
					this.setState({ searchAllResult: [] })
					this.setState({ searchAllSelection: "" })
			}
		}
	}

	// Handler
	handleCompareButtonClick(e, type) {
		// TODO: check if empty

		if(type === "artist") {
			console.log("Compare " + this.state.searchArtist1 + " and " + this.state.searchArtist2)
		} 
		else {
			console.log("Compare " + this.state.searchSong1 + " and " + this.state.searchSong2)
		}
	}

	/////////////////////////////////////////

	render() {
		const { classes } = this.props

		return (
			<div className="App" style={{ height : '500px' }}>

				<AppBar position="static">
					<Toolbar>
						<Typography className={classes.title} variant="h6" noWrap>
							Material-UI
						</Typography>
					</Toolbar>
				</AppBar>

				<Button 
					variant="outlined"
					onClick={ this.getState.bind(this) }>
						Get state
				</Button>

				<div>
					<h3>Chercher un artiste ou une chanson</h3>
					<Autocomplete
						freeSolo
						options={ this.state.searchAllResult.map(option => this.searchOptionRendering(option)) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e) }
						renderInput={ params => (
							<TextField
								id="searchAllTextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Recherche"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchAll(e) }
							/>
						) }
					/>
				</div>

				<div>
					<h3>Comparer deux artistes</h3>
					<Autocomplete
						freeSolo
						options={ this.state.searchArtist1Result.map(option => this.searchOptionRendering(option, "artist")) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e, "artist1", "artist") }
						renderInput={ params => (
							<TextField
								id="artist1TextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Artiste #1"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchArtist(e, "artist1") }
							/>
						) }
					/>
					<Autocomplete
						freeSolo
						options={ this.state.searchArtist2Result.map(option => this.searchOptionRendering(option, "artist")) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e, "artist2", "artist") }
						renderInput={ params => (
							<TextField
								id="artist2TextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Artiste #2"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchArtist(e, "artist2") }
							/>
						) }
					/>
					<Button variant="outlined" onClick={ e => this.handleCompareButtonClick(e, "artist") }>Comparer</Button>
				</div>

				<div>
					<h3>Comparer deux chansons</h3>
					<Autocomplete
						freeSolo
						options={ this.state.searchSong1Result.map(option => this.searchOptionRendering(option, "song")) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e, "song1", "song") }
						renderInput={ params => (
							<TextField
								id="song1TextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Chanson #1"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchSong(e, "song1") }
							/>
						) }
					/>
					<Autocomplete
						freeSolo
						options={ this.state.searchSong2Result.map(option => this.searchOptionRendering(option, "song")) }
						filterOptions={ options => { return options } }
						onChange={ e => this.handleSearchOptionSelection(e, "song2", "song") }
						renderInput={ params => (
							<TextField
								id="song2TextField"
								{...params}
								inputRef={ (input) => this.input = input }
								style={ { width : '300px' } }
								label="Chanson #2"
								margin="normal"
								variant="outlined"
								onChange={ e => this.searchSong(e, "song2") }
							/>
						) }
					/>
					<Button variant="outlined" onClick={ e => this.handleCompareButtonClick(e, "song") }>Comparer</Button>
				</div>

			</div>
		)
	}
}

export default withStyles(styles)(App)
