import React from 'react';
import './App.css';

import { withStyles } from '@material-ui/core/styles';
import { Paper, Container, Typography, TextField, Button } from '@material-ui/core';

const styles = theme => ({
	container: {
		display: 'flex',
		alignItems: 'center',
		//flexWrap: 'wrap',
	},
	textField: {
		marginLeft: theme.spacing(1),
		marginRight: theme.spacing(1),
		//width: 200,
	},
});

class App extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			input: ''
		};
	}

	state = {
		input: ''
	};

	getArtistData(){
		console.log("Artiste : ");
		console.log(this.state.input);
	}

	render() {
		const { classes } = this.props;
		state = {
			input: ''
		};

		/*return (
			<div className="App">
				<Container className={classes.container}>
					<Paper>
						<Typography variant="body1" gutterBottom>Comparer deux artistes :</Typography>
						<TextField
							className={classes.textField}
							label="Artiste 1"
							margin="normal"/>
						<TextField
							className={classes.textField}
							label="Artiste 2"
							margin="normal"/>
					</Paper>
				</Container>
			</div>
		);*/

		return(
			<div className="App">
				<Container className={classes.container}>
					<Typography variant="body1" gutterBottom>Artiste :</Typography>
					<input 
						type="text"
						value={this.state.input}
						onChange={this.getArtistData}
					/>
					<Button variant="contained" onClick={this.getArtistData}>Chercher</Button>
				</Container>
			</div>
		);
	}

}

export default withStyles(styles)(App);
