import React, { Component } from 'react'
import { withStyles, fade } from '@material-ui/core/styles';

import { TextField, AppBar, Toolbar, Typography } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab'

import { ResponsiveBar } from '@nivo/bar'
import { ResponsiveBubble } from '@nivo/circle-packing'

import './App.css'

/////////////////////////////////////////

const styles = theme => ({
	search: {
		position: 'relative',
		borderRadius: theme.shape.borderRadius,
		backgroundColor: fade(theme.palette.common.white, 0.15),
		'&:hover': {
			backgroundColor: fade(theme.palette.common.white, 0.25),
		},
		marginRight: theme.spacing(2),
		marginLeft: 0,
		width: '100%',
		[theme.breakpoints.up('sm')]: {
			marginLeft: theme.spacing(3),
			width: 'auto',
		},
	},
})

/////////////////////////////////////////

class App extends Component {

	constructor(props) {
		super(props)

		this.state = {

			topGenres: [],

			searchAll: "",
			searchAllResult: [],

			searchArtist1: "",
			searchArtist1Result: [],

			searchArtist2: "",
			searchArtist2Result: [],

			searchSong1: "",
			searchSong1Result: [],

			searchSong2: "",
			searchSong2Result: [],
		}
	}

	/////////////////////////////////////////

	componentDidMount() {
		//this.getGenrePopularity(10);
	}

	getState() {
		console.log(this.state)
	}

	searchArtistData() {
		const searchedArtist = this.input.value.trim()
		this.setState({ artist: searchedArtist })

		const request = `https://wasabi.i3s.unice.fr/api/v1/artist/name/${encodeURI(searchedArtist)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ artistData: data })
				else
					this.setState({ artistData: {} })
				console.log(this.state.artistData)
			})
	}

	getGenrePopularity(limit) {
		let request = `https://wasabi.i3s.unice.fr/api/v1/artist/genres/popularity`
		if(limit)
			request += `?limit=${limit}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ topGenres: data })
				else
					this.setState({ topGenres: {} })
				console.log(this.state.topGenres)
			})

		const chartData = this.state.topGenres
		const bubbles = {
			_id: "Genres",
			children: this.state.topGenres
		}

		return(
			<div>
				<ResponsiveBar
					data={chartData}
					indexBy="_id"
					keys={[ 'sum' ]}
					layout="horizontal"
					margin={{ top: 50, right: 50, bottom: 50, left: 150 }}
					colors={{ scheme: 'category10' }}
					borderRadius={10}
					enableGridY={false}
					axisBottom={null}
					axisLeft={{
						tickSize: 0,
						tickPadding: 10
					}}
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					isInteractive={false}
				/>
				<ResponsiveBubble
					root={bubbles}
					identity="_id"
					value="sum"
					leavesOnly={true}
					padding={5}
					margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
					colors={{ scheme: 'paired' }}
					colorBy='name'
					labelTextColor={{ from: 'color', modifiers: [ [ 'darker', '2' ] ] }}
					labelSkipRadius={0}
				/>
			</div>
		)
	}

	/////////////////////////////////////////

	// Requête sur l'API Wasabi : recherche d'artistes et chansons
	getArtistOrSongBySearch(search) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				if(data != null)
					this.setState({ searchAllResult: data })
				else
					this.setState({ searchAllResult: [] })
				console.log(this.state.searchAllResult)
			})
	}

	// Requête sur l'API Wasabi : recherche d'artistes
	getArtistBySearch(search, artistId) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				let searchResult
				if(data != null)
					searchResult = data.filter(option => !option.title)
				else
					searchResult = []
				if(artistId === "artist1")
					this.setState({ searchArtist1Result: searchResult })
				else
					this.setState({ searchArtist2Result: searchResult })
			})
	}

	// Requête sur l'API Wasabi : recherche de chansons
	getSongBySearch(search, songId) {
		const request = `https://wasabi.i3s.unice.fr/search/fulltext/${encodeURI(search)}`
		console.log(request)

		fetch(request)
			.then((response) => {
				return response.json()
			})
			.then((data) => {
				let searchResult
				if(data != null)
					searchResult = data.filter(option => option.title)
				else
					searchResult = []
				if(songId === "song1")
					this.setState({ searchSong1Result: searchResult })
				else
					this.setState({ searchSong2Result: searchResult })
			})
	}

	// Handler de la recherche d'artiste ou chanson (TextField onChange)
	searchAll(e) {
		const value = e.target.value.trim()
		this.setState({ searchAll: value })
		console.log("Search: " + value)
		if(value)
			this.getArtistOrSongBySearch(value)
		else
			this.setState({ searchAllResult: [] })
	}

	// Handler de la recherche d'artiste (TextField onChange)
	searchArtist(e, artistId) {
		const value = e.target.value.trim()
		if(artistId === "artist1")
			this.setState({ searchArtist1: value })
		else
			this.setState({ searchArtist2: value })
		console.log("Search (" + artistId + "): " + value)
		if(value)
			this.getArtistBySearch(value, artistId)
		else {
			if(artistId === "artist1")
				this.setState({ searchArtist1Result: [] })
			else
				this.setState({ searchArtist2Result: [] })
			console.log("[]")
		}
	}

	// Handler de la recherche de chanson (TextField onChange)
	searchSong(e, songId) {
		const value = e.target.value.trim()
		if(songId === "song1")
			this.setState({ searchSong1: value })
		else
			this.setState({ searchSong2: value })
		console.log("Search (" + songId + "): " + value)
		if(value)
			this.getSongBySearch(value, songId)
		else {
			if(songId === "song1")
				this.setState({ searchSong1Result: [] })
			else
				this.setState({ searchSong2Result: [] })
			console.log("[]")
		}
	}

	// Gère l'affichage d'un élément suggéré (en paramètre)
	searchOptionRendering(option, type) {
		if(type === "artist")
			return option.name
		if(option.title)
			return `${option.title} (chanson de ${option.name} de l'album ${option.albumTitle})`
		else
			return `${option.name} (artiste)`
	}

	// Handler de la sélection d'un élément suggéré
	handleSearchOptionSelection(e, field, type) {
		//console.log(e.target)
		const selection = e.target.textContent
		if(selection) {
			if(!type) {
				let split = selection.split("(")
				let value = split[0].trim()
				this.setState({ searchAll: value })
				if(split[1] === "artiste)")
					console.log("You've searched an artist: " + value)
				else
					console.log("You've searched a song: " + value)
			}
			else {
				let value = e.target.textContent
				switch(field) {
					case "artist1":
						this.setState({ searchArtist1: value })
						break
					case "artist2":
						this.setState({ searchArtist2: value })
						break
					case "song1":
						this.setState({ searchSong1: value })
						break
					case "song2":
						this.setState({ searchSong2: value })
						break
					default:
